      res.status(500).json({ message: error.message });
    }
  });

  app.post("/api/contacts", async (req, res) => {
    try {
      const contactData = req.body;
      const contact = await storage.createContact(contactData);
      res.json(contact);
    } catch (error: any) {
      console.error("Create contact error:", error);
      res.status(400).json({ message: error.message });
    }
  });

  app.get("/api/contacts/:id/vcard", async (req, res) => {
    try {
      const contact = await storage.getContact(req.params.id);
      if (!contact) {
        return res.status(404).json({ message: "Contact not found" });
      }

      const vcard = generateVCard(contact);
      const filename = `${contact.firstName || 'contact'}_${contact.lastName || ''}.vcf`.replace(/\s+/g, '_');

      res.setHeader('Content-Type', 'text/vcard; charset=utf-8');
      res.setHeader('Content-Disposition', `attachment; filename="${filename}"`);
      res.send(vcard);
    } catch (error: any) {
      console.error("VCard generation error:", error);
      res.status(500).json({ message: error.message });
    }
  });

  // Enhanced OCR endpoint for business card processing
  app.post("/api/ocr/business-card", upload.single("image"), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "Image file is required" });
      }

      const { userId } = req.body;
      if (!userId) {
        return res.status(400).json({ message: "User ID is required" });
      }

      // Convert image buffer to base64
      const base64Image = req.file.buffer.toString('base64');

      // Extract text using OpenAI Vision
      const extractedText = await extractTextFromImage(base64Image);

      // Extract contact information
      const contactInfo = extractContactFromText(extractedText);

      // Create the contact
      const newContact = await storage.createContact({
        ...contactInfo,
        userId,
        source: "business_card"
      });

      // Create follow-up reminder
      const followUpTitle = `Follow up with ${newContact.firstName || 'new contact'}${newContact.lastName ? ' ' + newContact.lastName : ''}`;
      await storage.createReminder({
        userId,
        title: followUpTitle,
        description: `Contact info from business card:\n${extractedText}`,
        dueDate: new Date(Date.now() + 24 * 60 * 60 * 1000), // Tomorrow
        category: "Follow-up"
      });

      res.json({
        contact: newContact,
        extractedText,
        message: `Contact saved and follow-up reminder created for ${newContact.firstName || 'contact'}`
      });
    } catch (error: any) {
      console.error("Business card OCR error:", error);
      res.status(500).json({ message: error.message });
    }
  });

  // Link shortener routes for affiliate monetization
  app.post("/api/shorten", async (req, res) => {
    try {
      const { url } = req.body;
      if (!url) {
        return res.status(400).json({ message: "URL is required" });
      }

      const { shortCode, shortUrl } = createShortLink(url);
      res.json({ shortCode, shortUrl, originalUrl: url });
    } catch (error: any) {
      console.error("Link shortening error:", error);
      res.status(500).json({ message: "Failed to shorten link" });
    }
  });

  // Test endpoint to verify affiliate link processing
  app.post("/api/test-affiliate", async (req, res) => {
    try {
      const { text } = req.body;
      if (!text) {
        return res.status(400).json({ message: "Text content is required" });
      }

      const processedText = await processUrlsInContent(text);
      res.json({ 
        original: text,
        processed: processedText,
        affiliateCode: "floater01b-20"
      });
    } catch (error: any) {
      console.error("Affiliate test error:", error);
      res.status(500).json({ message: "Failed to process affiliate links" });
    }
  });

  // Link redirect endpoint (handles short link clicks)
  app.get("/l/:shortCode", async (req, res) => {
    try {
      const { shortCode } = req.params;
      const longUrl = getLongUrl(shortCode);

      if (!longUrl) {
        return res.status(404).json({ message: "Link not found or expired" });
      }

      // Redirect to the original (affiliate) URL
      res.redirect(302, longUrl);
    } catch (error: any) {
      console.error("Link redirect error:", error);
      res.status(500).json({ message: "Redirect failed" });
    }
  });

  // Link analytics endpoint (optional)
  app.get("/api/link-stats", async (req, res) => {
    try {
      const stats = getLinkStats();
      res.json(stats);
    } catch (error: any) {
      console.error("Link stats error:", error);
      res.status(500).json({ message: "Failed to get stats" });
    }
  });

  // Analytics dashboard (custom built-in dashboard)
  app.get("/analytics", (req, res) => {
    const analyticsHTML = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>GabAi Analytics Dashboard</title>
    <style>
        body { font-family: system-ui, -apple-system, sans-serif; margin: 0; padding: 20px; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); min-height: 100vh; }
        .container { max-width: 1200px; margin: 0 auto; }
        .header { text-align: center; color: white; margin-bottom: 40px; }
        .header h1 { font-size: 2.5rem; margin: 0; text-shadow: 2px 2px 4px rgba(0,0,0,0.3); }
        .metrics-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 24px; margin-bottom: 40px; }
        .metric-card { background: white; border-radius: 16px; padding: 24px; box-shadow: 0 8px 32px rgba(0,0,0,0.1); }
        .metric-value { font-size: 2.5rem; font-weight: 700; margin-bottom: 8px; }
        .users { color: #3b82f6; } .messages { color: #10b981; } .lists { color: #8b5cf6; } .clicks { color: #f59e0b; }
        .refresh-btn { background: rgba(255,255,255,0.2); color: white; border: 1px solid rgba(255,255,255,0.3); padding: 8px 16px; border-radius: 8px; cursor: pointer; }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <h1>üéØ GabAi Analytics Dashboard</h1>
            <p>Real-time insights ‚Ä¢ Voice-first AI assistant</p>
            <button class="refresh-btn" onclick="loadAnalytics()">‚Üª Refresh Data</button>
        </div>
        <div class="metrics-grid">
            <div class="metric-card">
                <div class="metric-value users" id="totalUsers">Loading...</div>
                <div>Total Users</div>
            </div>
            <div class="metric-card">
                <div class="metric-value messages" id="totalMessages">Loading...</div>
                <div>Messages Sent</div>
            </div>
            <div class="metric-card">
                <div class="metric-value lists" id="totalLists">Loading...</div>
                <div>Smart Lists</div>
            </div>
            <div class="metric-card">
                <div class="metric-value clicks" id="linkClicks">Loading...</div>
                <div>Link Clicks</div>
            </div>
        </div>
        <div style="background: white; border-radius: 16px; padding: 24px; text-align: center;">
            <h3 style="color: #374151;">‚úÖ Production Status: All Systems Operational</h3>
            <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 16px; margin-top: 20px;">
                <div>üéôÔ∏è Voice AI Ready<br><small>6 alarm personalities</small></div>
                <div>üì± Mobile Native<br><small>Capacitor integration</small></div>
                <div>üí∞ Monetization Active<br><small>URL shortening + tracking</small></div>
                <div>üìä Analytics Live<br><small>Real-time metrics</small></div>
            </div>
            <p style="margin-top: 30px; color: #10b981; font-weight: 600; font-size: 1.2rem;">
                Ready for $14.95/month Google Play Store launch üöÄ
            </p>
        </div>
    </div>
    <script>
        async function loadAnalytics() {
            try {
                const response = await fetch('/api/analytics/summary');
                const data = await response.json();
                document.getElementById('totalUsers').textContent = data.totalUsers || '0';
                document.getElementById('totalMessages').textContent = data.totalMessages || '0';
                document.getElementById('totalLists').textContent = data.totalLists || '0';
                document.getElementById('linkClicks').textContent = data.linkClicks || '0';
            } catch (error) {
                console.error('Error loading analytics:', error);
            }
        }
        loadAnalytics();
        setInterval(loadAnalytics, 30000);
    </script>
</body>
</html>`;
    res.send(analyticsHTML);
  });

  // Metabase analytics proxy endpoint (optional)
  app.get("/api/analytics/embed/:dashboardId", async (req, res) => {
    try {
      // This endpoint can be used to embed Metabase dashboards
      // For now, just redirect to the analytics setup page
      res.json({ 
        message: "Analytics dashboard available at http://localhost:3000",
        setup: "Run: docker run -d -p 3000:3000 metabase/metabase",
        dashboardId: req.params.dashboardId
      });
    } catch (error: any) {
      console.error("Analytics embed error:", error);
      res.status(500).json({ message: "Failed to load analytics" });
    }
  });

  // Simple analytics data endpoint for Metabase
  app.get("/api/analytics/summary", async (req, res) => {
    try {
      const summary = {
        totalUsers: await storage.getUserCount(),
        totalMessages: await storage.getMessageCount(),
        totalLists: await storage.getSmartListCount(),
        linkClicks: getLinkStats().totalClicks,
        timestamp: new Date().toISOString()
      };

      res.json(summary);
    } catch (error: any) {
      console.error("Analytics summary error:", error);
      res.status(500).json({ message: "Failed to get analytics summary" });
    }
  });

  // Mount ElevenLabs routes for AI voice generation
  app.use('/api/elevenlabs', elevenlabsRouter);

  // Mount APK download routes for beta testing
  registerApkRoutes(app);

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  conversations,
  messages,
  smartLists,
  listItems,
  reminders,
  contacts,
  type User,
  type InsertUser,
  type Conversation,
  type InsertConversation,
  type Message,
  type InsertMessage,
  type SmartList,
  type InsertSmartList,
  type ListItem,
  type InsertListItem,
  type Reminder,
  type InsertReminder,
  type Contact,
  type InsertContact,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User>;

  // Conversation operations
  getConversations(userId: string): Promise<Conversation[]>;
  createConversation(conversation: InsertConversation): Promise<Conversation>;
  getConversation(id: string): Promise<Conversation | undefined>;

  // Message operations
  getMessages(conversationId: string): Promise<Message[]>;
  createMessage(message: InsertMessage): Promise<Message>;

  // Smart list operations
  getSmartLists(userId: string): Promise<(SmartList & { items: ListItem[] })[]>;
  createSmartList(list: InsertSmartList): Promise<SmartList>;
  getSmartList(id: string): Promise<(SmartList & { items: ListItem[] }) | undefined>;
  updateSmartList(id: string, updates: Partial<InsertSmartList>): Promise<SmartList>;
  deleteSmartList(id: string): Promise<void>;
  getSharedList(shareCode: string): Promise<(SmartList & { items: ListItem[] }) | undefined>;
  shareList(id: string): Promise<string>; // Returns share code
  joinSharedList(shareCode: string, userId: string): Promise<SmartList>;
  addCollaborator(listId: string, collaboratorId: string): Promise<SmartList>;

  // List item operations
  createListItem(item: InsertListItem): Promise<ListItem>;
  updateListItem(id: string, updates: Partial<InsertListItem>): Promise<ListItem>;
  deleteListItem(id: string): Promise<void>;

  // Reminder operations
  getReminders(userId: string): Promise<Reminder[]>;
  getReminder(id: string): Promise<Reminder | undefined>;
  createReminder(reminder: InsertReminder): Promise<Reminder>;
  updateReminder(id: string, updates: Partial<InsertReminder>): Promise<Reminder>;
  deleteReminder(id: string): Promise<void>;

  // Contact operations
  getContacts(userId: string): Promise<Contact[]>;
  createContact(contact: InsertContact): Promise<Contact>;
  getContact(id: string): Promise<Contact | undefined>;
  updateContact(id: string, updates: Partial<InsertContact>): Promise<Contact>;
  deleteContact(id: string): Promise<void>;

  // Admin analytics operations
  getUserCount(): Promise<number>;
  getConversationCount(): Promise<number>;
  getMessageCount(): Promise<number>;
  getSmartListCount(): Promise<number>;
  getListItemCount(): Promise<number>;
  getReminderCount(): Promise<number>;
  getContactCount(): Promise<number>;
  getRecentUsers(limit: number): Promise<Array<{
    id: string;
    name: string;
    email: string;
    createdAt: string;
    lastActive: string;
    messageCount: number;
  }>>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    console.log('Looking up user with ID:', id);
    const [user] = await db.select().from(users).where(eq(users.id, id));
    console.log('Found user:', user);
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const userData = {
      ...insertUser,
      preferences: insertUser.preferences as any
    };
    const [user] = await db.insert(users).values([userData]).returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User> {
    const updateData = {
      ...updates,
      preferences: updates.preferences as any,
      updatedAt: new Date()
    };
    const [user] = await db
      .update(users)
      .set(updateData)
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Conversation operations
  async getConversations(userId: string): Promise<Conversation[]> {
    return await db
      .select()
      .from(conversations)
      .where(eq(conversations.userId, userId))
      .orderBy(desc(conversations.updatedAt));
  }

  async createConversation(insertConversation: InsertConversation): Promise<Conversation> {
    const [conversation] = await db
      .insert(conversations)
      .values(insertConversation)
      .returning();
    return conversation;
  }

  async getConversation(id: string): Promise<Conversation | undefined> {
    const [conversation] = await db
      .select()
      .from(conversations)
      .where(eq(conversations.id, id));
    return conversation;
  }

  // Message operations
  async getMessages(conversationId: string): Promise<Message[]> {
    return await db
      .select()
      .from(messages)
      .where(eq(messages.conversationId, conversationId))
      .orderBy(messages.createdAt);
  }

  async createMessage(insertMessage: InsertMessage): Promise<Message> {
    const messageData = {
      ...insertMessage,
      role: insertMessage.role as "user" | "assistant"
    };
    const [message] = await db.insert(messages).values([messageData]).returning();
    return message;
  }

  // Smart list operations
  async getSmartLists(userId: string): Promise<(SmartList & { items: ListItem[] })[]> {
    // Get lists owned by user + lists where user is a collaborator
    const ownedLists = await db
      .select()
      .from(smartLists)
      .where(eq(smartLists.userId, userId))
      .orderBy(desc(smartLists.updatedAt));

    // For now, only return owned lists to avoid JSONB query issues
    // TODO: Add collaborative lists support when needed
    const allLists = [...ownedLists];

    const listsWithItems = await Promise.all(
      allLists.map(async (list) => {
        const items = await db
          .select()
          .from(listItems)
          .where(eq(listItems.listId, list.id))
          .orderBy(listItems.position, listItems.createdAt);
        return { ...list, items };
      })
    );

    return listsWithItems;
  }

  async createSmartList(insertList: InsertSmartList): Promise<SmartList> {
    const listData = {
      ...insertList,
      shareCode: insertList.isShared ? `${Date.now()}-${Math.random().toString(36).substr(2, 9)}` : null,
      collaborators: insertList.collaborators as any,
      categories: insertList.categories as any
    };
    const [list] = await db.insert(smartLists).values([listData]).returning();
    return list;
  }

  async getSmartList(id: string): Promise<(SmartList & { items: ListItem[] }) | undefined> {
    const [list] = await db
      .select()
      .from(smartLists)
      .where(eq(smartLists.id, id));

    if (!list) return undefined;

    const items = await db
      .select()
      .from(listItems)
      .where(eq(listItems.listId, id))
      .orderBy(listItems.position, listItems.createdAt);

    return { ...list, items };
  }

  async updateSmartList(id: string, updates: Partial<InsertSmartList>): Promise<SmartList> {
    const updateData = {
      ...updates,
      collaborators: updates.collaborators as any,
      categories: updates.categories as any,
      updatedAt: new Date()
    };
    const [list] = await db
      .update(smartLists)
      .set(updateData)
      .where(eq(smartLists.id, id))
      .returning();
    return list;
  }

  async getSharedList(shareCode: string): Promise<(SmartList & { items: ListItem[] }) | undefined> {
    const [list] = await db
      .select()
      .from(smartLists)
      .where(eq(smartLists.shareCode, shareCode));

    if (!list) return undefined;

    const items = await db
      .select()
      .from(listItems)
      .where(eq(listItems.listId, list.id))
      .orderBy(listItems.position, listItems.createdAt);

    return { ...list, items };
  }

  async addCollaborator(listId: string, collaboratorId: string): Promise<SmartList> {
    const [list] = await db
      .select()
      .from(smartLists)
      .where(eq(smartLists.id, listId));

    if (!list) throw new Error("List not found");

    const collaborators = [...(list.collaborators || []), collaboratorId];

    const [updatedList] = await db
      .update(smartLists)
      .set({ 
        collaborators: collaborators as any,
        updatedAt: new Date() 
      })
      .where(eq(smartLists.id, listId))
      .returning();

    return updatedList;
  }

  // List item operations
  async createListItem(insertItem: InsertListItem): Promise<ListItem> {
    const [item] = await db.insert(listItems).values([insertItem]).returning();
    return item;
  }

  async updateListItem(id: string, updates: Partial<InsertListItem>): Promise<ListItem> {
    const [item] = await db
      .update(listItems)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(listItems.id, id))
      .returning();
    return item;
  }

  async deleteListItem(id: string): Promise<void> {
    await db.delete(listItems).where(eq(listItems.id, id));
  }

  async toggleListItem(id: string): Promise<ListItem> {
    // Get the current item
    const [currentItem] = await db
      .select()
      .from(listItems)
      .where(eq(listItems.id, id));

    if (!currentItem) {
      throw new Error("List item not found");
    }

    // Toggle completed status
    const [item] = await db
      .update(listItems)
      .set({ 
        completed: !currentItem.completed,
        updatedAt: new Date() 
      })
      .where(eq(listItems.id, id))
      .returning();

    return item;
  }

  // Reminder operations
  async getReminders(userId: string): Promise<Reminder[]> {
    return await db
      .select()
      .from(reminders)
      .where(eq(reminders.userId, userId))
      .orderBy(reminders.dueDate);
  }

  async getReminder(id: string): Promise<Reminder | undefined> {
    const [reminder] = await db
      .select()
      .from(reminders)
      .where(eq(reminders.id, id));
    return reminder;
  }

  async createReminder(insertReminder: InsertReminder): Promise<Reminder> {
    const [reminder] = await db.insert(reminders).values(insertReminder).returning();
    return reminder;
  }

  async updateReminder(id: string, updates: Partial<InsertReminder>): Promise<Reminder> {
    const [reminder] = await db
      .update(reminders)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(reminders.id, id))
      .returning();
    return reminder;
  }

  async deleteReminder(id: string): Promise<void> {
    await db.delete(reminders).where(eq(reminders.id, id));
  }

  // Additional smart list methods
  async deleteSmartList(id: string): Promise<void> {
    // Delete all items first
    await db.delete(listItems).where(eq(listItems.listId, id));
    // Then delete the list
    await db.delete(smartLists).where(eq(smartLists.id, id));
  }

  async shareSmartList(id: string): Promise<string> {
    const shareCode = Math.random().toString(36).substring(2, 10).toUpperCase();
    await db
      .update(smartLists)
      .set({ 
        shareCode,
        isShared: true,
        updatedAt: new Date() 
      })
      .where(eq(smartLists.id, id));
    return shareCode;
  }

  async shareList(id: string): Promise<string> {
    // Alias for backward compatibility
    return this.shareSmartList(id);
  }

  async joinSharedList(shareCode: string, userId: string): Promise<SmartList> {
    const [list] = await db
      .select()
      .from(smartLists)
      .where(eq(smartLists.shareCode, shareCode));

    if (!list) throw new Error("List not found with this share code");

    // Add user as collaborator
    const collaborators = [...(list.collaborators || []), userId];

    const [updatedList] = await db
      .update(smartLists)
      .set({ 
        collaborators: collaborators as any,
        updatedAt: new Date() 
      })
      .where(eq(smartLists.id, list.id))
      .returning();

    return updatedList;
  }

  // Contact operations
  async getContacts(userId: string): Promise<Contact[]> {
    return await db
      .select()
      .from(contacts)
      .where(eq(contacts.userId, userId))
      .orderBy(desc(contacts.createdAt));
  }

  async createContact(insertContact: InsertContact): Promise<Contact> {
    const [contact] = await db.insert(contacts).values([insertContact]).returning();
    return contact;
  }

  async getContact(id: string): Promise<Contact | undefined> {
    const [contact] = await db.select().from(contacts).where(eq(contacts.id, id));
    return contact;
  }

  async updateContact(id: string, updates: Partial<InsertContact>): Promise<Contact> {
    const [contact] = await db
      .update(contacts)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(contacts.id, id))
      .returning();
    return contact;
  }

  async deleteContact(id: string): Promise<void> {
    await db.delete(contacts).where(eq(contacts.id, id));
  }

  // Admin analytics operations
  async getUserCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(users);
    return result[0]?.count || 0;
  }

  async getConversationCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(conversations);
    return result[0]?.count || 0;
  }

  async getMessageCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(messages);
    return result[0]?.count || 0;
  }

  async getSmartListCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(smartLists);
    return result[0]?.count || 0;
  }

  async getListItemCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(listItems);
    return result[0]?.count || 0;
  }

  async getReminderCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(reminders);
    return result[0]?.count || 0;
  }

  async getContactCount(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(contacts);
    return result[0]?.count || 0;
  }

  async getRecentUsers(limit: number): Promise<Array<{
    id: string;
    name: string;
    email: string;
    createdAt: string;
    lastActive: string;
    messageCount: number;
  }>> {
    const recentUsers = await db
      .select({
        id: users.id,
        email: users.email,
        firstName: users.firstName,
        lastName: users.lastName,
        createdAt: users.createdAt,
        updatedAt: users.updatedAt,
      })
      .from(users)
      .orderBy(desc(users.createdAt))
      .limit(limit);

    // Get message counts for each user
    const usersWithStats = await Promise.all(
      recentUsers.map(async (user) => {
        const messageCount = await db
          .select({ count: sql<number>`count(*)` })
          .from(messages)
          .innerJoin(conversations, eq(messages.conversationId, conversations.id))
          .where(eq(conversations.userId, user.id));

        return {
          id: user.id,
          name: `${user.firstName || ''} ${user.lastName || ''}`.trim() || 'Anonymous',
          email: user.email || 'No email',
          createdAt: user.createdAt?.toISOString() || new Date().toISOString(),
          lastActive: user.updatedAt?.toISOString() || user.createdAt?.toISOString() || new Date().toISOString(),
          messageCount: messageCount[0]?.count || 0,
        };
      })
    );

    return usersWithStats;
  }
}

export const storage = new DatabaseStorage();

