                  <div className="space-y-2">
                    <div>
                      <Label htmlFor="dev-name" className="text-xs">Name</Label>
                      <Input
                        id="dev-name"
                        value={devName}
                        onChange={(e) => setDevName(e.target.value)}
                        placeholder="Your name"
                        className="h-8 text-sm"
                      />
                    </div>
                    <div>
                      <Label htmlFor="dev-email" className="text-xs">Email</Label>
                      <Input
                        id="dev-email"
                        type="email"
                        value={devEmail}
                        onChange={(e) => setDevEmail(e.target.value)}
                        placeholder="your@email.com"
                        className="h-8 text-sm"
                      />
                    </div>
                    <Button
                      onClick={handleDevLogin}
                      disabled={!devName.trim() || !devEmail.trim() || isDevLoggingIn}
                      className="w-full h-8 text-xs"
                      size="sm"
                    >
                      {isDevLoggingIn ? "Signing in..." : "Sign In (Dev)"}
                    </Button>
                  </div>
                </div>
              )}
            </div>
          )}

          {/* Always show install button for debugging */}
          <div className="space-y-3">
            <div className="flex items-center justify-center space-x-2">
              <Smartphone className="h-4 w-4 text-blue-600" />
              <Badge variant="secondary" className="bg-blue-50 text-blue-700 border-blue-200">
                Install Available
              </Badge>
            </div>
            <Button 
              variant="outline" 
              className="w-full" 
              onClick={handleInstallClick}
            >
              <Download className="h-4 w-4 mr-2" />
              Install GabAi App
            </Button>
            <p className="text-xs text-muted-foreground text-center">
              Install for faster access and offline support
            </p>
          </div>

          <div className="text-center text-sm text-muted-foreground">
            <p>Sign in to sync your data across all devices</p>
          </div>
        </CardContent>
      </Card>

      {/* Bottom Navigation - only show if there's intent to navigate */}
      <div className="mt-8 text-center text-xs text-gray-500">
        <p>Access all GabAi features after logging in</p>
      </div>
    </div>
  );
}

